        var origin = l.protocol + '//' + l.hostname + (l.port ? ':' + l.port : '');·
        // Split pathname into segments (skip leading empty segment from the leading slash)
        var segments = l.pathname.split('/').filter(function (s) { return s.length > 0; });·
